// ======================
// VARIABLES GLOBALES
// ======================
let mobiliario = JSON.parse(localStorage.getItem("mobiliario")) || [];
let editIndex = null;

// ======================
// RENDER TABLA
// ======================
function renderTabla(data = mobiliario) {
  const tbody = document.getElementById("tabla-body");
  tbody.innerHTML = "";

  data.forEach((item, index) => {
    const tr = document.createElement("tr");

    tr.innerHTML = `
      <td>${item.nombre}</td>
      <td>${item.codigo}</td>
      <td>${item.descripcion}</td>
      <td>${item.tipo}</td>
      <td>${item.ubicacion}</td>
      <td>${item.estado}</td>
      <td>${item.cantidad}</td>
      <td class="acciones">
        <button class="btn editar" onclick="abrirEditar(${index})">Editar</button>
        <button class="btn eliminar" onclick="eliminarMobiliario(${index})">Eliminar</button>
      </td>
    `;
    tbody.appendChild(tr);
  });
}

// ======================
// MODAL
// ======================
function abrirModal() {
  document.getElementById("modal").classList.add("activo");
}

function cerrarModal() {
  document.getElementById("modal").classList.remove("activo");
  document.getElementById("form-mobiliario").reset();
  editIndex = null;
}

// ======================
// AGREGAR / EDITAR
// ======================
document.getElementById("form-mobiliario").addEventListener("submit", function (e) {
  e.preventDefault();

  const item = {
    nombre: nombre.value,
    codigo: codigo.value,
    descripcion: descripcion.value,
    tipo: tipo.value,
    ubicacion: ubicacion.value,
    estado: estado.value,
    cantidad: cantidad.value
  };

  if (editIndex === null) {
    mobiliario.push(item);
  } else {
    mobiliario[editIndex] = item;
  }

  localStorage.setItem("mobiliario", JSON.stringify(mobiliario));
  cerrarModal();
  renderTabla();
});

// ======================
// EDITAR
// ======================
function abrirEditar(index) {
  editIndex = index;
  const item = mobiliario[index];

  nombre.value = item.nombre;
  codigo.value = item.codigo;
  descripcion.value = item.descripcion;
  tipo.value = item.tipo;
  ubicacion.value = item.ubicacion;
  estado.value = item.estado;
  cantidad.value = item.cantidad;

  abrirModal();
}

// ======================
// ELIMINAR
// ======================
function eliminarMobiliario(index) {
  if (confirm("¿Deseas eliminar este mobiliario?")) {
    mobiliario.splice(index, 1);
    localStorage.setItem("mobiliario", JSON.stringify(mobiliario));
    renderTabla();
  }
}

// ======================
// FILTROS
// ======================
function aplicarFiltro() {
  const texto = filtroTexto.value.toLowerCase();
  const tipoF = filtroTipo.value;
  const ubicacionF = filtroUbicacion.value;
  const estadoF = filtroEstado.value;

  const filtrado = mobiliario.filter(item =>
    (item.nombre.toLowerCase().includes(texto) ||
      item.codigo.toLowerCase().includes(texto) ||
      item.descripcion.toLowerCase().includes(texto)) &&
    (tipoF === "" || item.tipo === tipoF) &&
    (ubicacionF === "" || item.ubicacion === ubicacionF) &&
    (estadoF === "" || item.estado === estadoF)
  );

  renderTabla(filtrado);
}

function limpiarFiltro() {
  filtroTexto.value = "";
  filtroTipo.value = "";
  filtroUbicacion.value = "";
  filtroEstado.value = "";
  renderTabla();
}

// ======================
renderTabla();
