************************/
let inventario = JSON.parse(localStorage.getItem("inventario")) || [];
let usuarioActivo = JSON.parse(localStorage.getItem("usuarioActivo")) || null;
let editIndex = null;

/***********************
  ELEMENTOS DEL DOM
************************/
const root = document.getElementById("root");

/***********************
  LOGIN
************************/
function renderLogin() {
  root.innerHTML = `
    <div class="login-page">
      <div class="login-card">
        <h2>Acceso al Sistema</h2>
        <input id="usuario" placeholder="Usuario" />
        <input id="password" type="password" placeholder="Contraseña" />
        <button onclick="login()">Ingresar</button>
      </div>
    </div>
  `;
}

function login() {
  const usuario = document.getElementById("usuario").value;
  const password = document.getElementById("password").value;

  if (usuario === "admin" && password === "1234") {
    usuarioActivo = { usuario };
    localStorage.setItem("usuarioActivo", JSON.stringify(usuarioActivo));
    renderSistema();
  } else {
    alert("Usuario o contraseña incorrectos");
  }
}

function logout() {
  localStorage.removeItem("usuarioActivo");
  usuarioActivo = null;
  renderLogin();
}

/***********************
  SISTEMA
************************/
function renderSistema() {
  root.innerHTML = `
    <div class="system-page">
      <header>
        <h1>Inventario de Mobiliario</h1>
        <button onclick="logout()">Cerrar sesión</button>
      </header>

      <div class="acciones">
        <input 
          type="text" 
          id="filtroBusqueda" 
          placeholder="Buscar por nombre, código o ubicación"
        />
        <button onclick="abrirModal()">Agregar</button>
        <button onclick="exportarPDF()">PDF</button>
        <button onclick="exportarExcel()">Excel</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Código</th>
            <th>Ubicación</th>
            <th>Estado</th>
            <th>Cantidad</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody></tbody>
      </table>

      <div id="modal" class="modal oculto">
        <div class="modal-content">
          <h2 id="modalTitulo">Agregar mobiliario</h2>

          <input id="nombre" placeholder="Nombre" />
          <input id="codigo" placeholder="Código" />
          <input id="ubicacion" placeholder="Ubicación" />
          <input id="cantidad" type="number" placeholder="Cantidad" />

