          </select>

          <div class="modal-actions">
            <button onclick="guardar()">Guardar</button>
            <button onclick="cerrarModal()">Cancelar</button>
          </div>
        </div>
      </div>
    </div>
  `;

  document
    .getElementById("filtroBusqueda")
    .addEventListener("input", renderInventario);

  renderInventario();
}

