import React, { useState } from "react";
import jsPDF from "jspdf";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import "./App.css";

const USERS = [
  { username: "admin", password: "admin123", role: "admin" },
  { username: "jefe", password: "jefe123", role: "jefe" },
  { username: "auxiliar", password: "aux123", role: "auxiliar" },
];

function App() {
  const [user, setUser] = useState(null);
  const [login, setLogin] = useState({ username: "", password: "" });

  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);

  const [filters, setFilters] = useState({
    text: "",
    tipo: "",
    ubicacion: "",
    estado: "",
  });

  const [modalOpen, setModalOpen] = useState(false);
  const [editingItem, setEditingItem] = useState(null);

  const handleLogin = () => {
    const found = USERS.find(
      (u) =>
        u.username === login.username && u.password === login.password
    );
    if (!found) return alert("Credenciales incorrectas");
    setUser(found);
  };

  const handleLogout = () => {
    setUser(null);
    setItems([]);
    setFilteredItems([]);
  };

  const saveItem = (item) => {
    if (editingItem) {
      const updated = items.map((i) =>
        i.id === item.id ? item : i
      );
      setItems(updated);
      setFilteredItems(updated);
    } else {
      const newItems = [...items, item];
      setItems(newItems);
      setFilteredItems(newItems);
    }
    setModalOpen(false);
    setEditingItem(null);
  };

  const handleDelete = (id) => {
    if (!window.confirm("¿Eliminar mobiliario?")) return;
    const updated = items.filter((i) => i.id !== id);
    setItems(updated);
    setFilteredItems(updated);
  };

  const applyFilters = () => {
    let result = items.filter((i) =>
      (filters.text === "" ||
        i.nombre.toLowerCase().includes(filters.text.toLowerCase()) ||
        i.codigo.toLowerCase().includes(filters.text.toLowerCase()) ||
        i.descripcion.toLowerCase().includes(filters.text.toLowerCase())) &&
      (filters.tipo === "" || i.tipo === filters.tipo) &&
      (filters.ubicacion === "" || i.ubicacion === filters.ubicacion) &&
      (filters.estado === "" || i.estado === filters.estado)
    );
    setFilteredItems(result);
  };

  const clearFilters = () => {
    setFilters({ text: "", tipo: "", ubicacion: "", estado: "" });
    setFilteredItems(items);
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text("Inventario de Biblioteca", 10, 10);
    let y = 20;
    filteredItems.forEach((i, index) => {
      doc.text(
        `${index + 1}. ${i.nombre} | ${i.codigo} | ${i.tipo} | ${i.ubicacion} | ${i.estado}`,
        10,
        y
      );
      y += 8;
    });
    doc.save("inventario.pdf");
  };

  const exportExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredItems);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Inventario");
    const buffer = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    saveAs(new Blob([buffer]), "inventario.xlsx");
  };

  if (!user) {
    return (
      <div className="login">
        <h2>Ingreso al Sistema</h2>
        <input
          placeholder="Usuario"
          onChange={(e) =>
            setLogin({ ...login, username: e.target.value })
          }
        />
        <input
          type="password"
          placeholder="Contraseña"
          onChange={(e) =>
            setLogin({ ...login, password: e.target.value })
          }
        />
        <button onClick={handleLogin}>Ingresar</button>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="header">
        <h2>Inventario Biblioteca</h2>
        <div>
          <strong>{user.role.toUpperCase()}</strong>
          <button className="logout" onClick={handleLogout}>
            Salir
          </button>
        </div>
      </header>

      <div className="filters">
        <input
          placeholder="Buscar por nombre, código o descripción"
          value={filters.text}
          onChange={(e) =>
            setFilters({ ...filters, text: e.target.value })
          }
        />
        <select
          onChange={(e) =>
            setFilters({ ...filters, tipo: e.target.value })
          }
        >
          <option value="">Tipo</option>
          <option>Mesa</option>
          <option>Silla</option>
          <option>Estante</option>
        </select>
        <select
          onChange={(e) =>
            setFilters({ ...filters, ubicacion: e.target.value })
          }
        >
          <option value="">Ubicación</option>
          <option>Sala</option>
          <option>Bodega</option>
        </select>
        <select
          onChange={(e) =>
            setFilters({ ...filters, estado: e.target.value })
          }
        >
          <option value="">Estado</option>
          <option>Bueno</option>
          <option>Regular</option>
          <option>Malo</option>
        </select>
        <button onClick={applyFilters}>Aplicar filtros</button>
        <button onClick={clearFilters}>Limpiar filtros</button>
      </div>

      <div className="actions">
        {user.role !== "auxiliar" && (
          <button
            className="add"
            onClick={() => setModalOpen(true)}
          >
            Agregar mobiliario
          </button>
        )}
        <button className="pdf" onClick={exportPDF}>
          Exportar PDF
        </button>
        <button className="excel" onClick={exportExcel}>
          Exportar Excel
        </button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Código</th>
            <th>Tipo</th>
            <th>Ubicación</th>
            <th>Estado</th>
            {user.role !== "auxiliar" && <th>Acciones</th>}
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((i) => (
            <tr key={i.id}>
              <td>{i.nombre}</td>
              <td>{i.codigo}</td>
              <td>{i.tipo}</td>
              <td>{i.ubicacion}</td>
              <td>{i.estado}</td>
              {user.role !== "auxiliar" && (
                <td>
                  <button onClick={() => { setEditingItem(i); setModalOpen(true); }}>
                    Editar
                  </button>
                  <button onClick={() => handleDelete(i.id)}>
                    Eliminar
                  </button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && (
        <Modal
          onSave={saveItem}
          onClose={() => {
            setModalOpen(false);
            setEditingItem(null);
          }}
          item={editingItem}
        />
      )}
    </div>
  );
}

function Modal({ onSave, onClose, item }) {
  const [data, setData] = useState(
    item || {
      id: Date.now(),
      nombre: "",
      codigo: "",
      descripcion: "",
      tipo: "",
      ubicacion: "",
      estado: "",
    }
  );

  return (
    <div className="modal">
      <div className="modal-box">
        <h3>{item ? "Editar" : "Agregar"} mobiliario</h3>
        {Object.keys(data).map(
          (k) =>
            k !== "id" && (
              <input
                key={k}
                placeholder={k}
                value={data[k]}
                onChange={(e) =>
                  setData({ ...data, [k]: e.target.value })
                }
              />
            )
        )}
        <button onClick={() => onSave(data)}>Guardar</button>
        <button onClick={onClose}>Cancelar</button>
      </div>
    </div>
  );
}

export default App;
