import React, { useState } from "react";
import jsPDF from "jspdf";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import "./App.css";

const USERS = [
  { username: "admin", password: "admin123", role: "admin" },
  { username: "jefe", password: "jefe123", role: "jefe" },
  { username: "aux", password: "aux123", role: "auxiliar" },
];

function App() {
  const [user, setUser] = useState(null);
  const [login, setLogin] = useState({ username: "", password: "" });
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [editingItem, setEditingItem] = useState(null);

  const handleLogin = () => {
    const found = USERS.find(
      (u) => u.username === login.username && u.password === login.password
    );
    if (!found) return alert("Credenciales incorrectas");
    setUser(found);
  };

  const handleDelete = (id) => {
    if (!window.confirm("¿Eliminar mobiliario?")) return;
    const updated = items.filter((i) => i.id !== id);
    setItems(updated);
    setFilteredItems(updated);
  };

  const saveItem = (item) => {
    const exists = items.find((i) => i.id === item.id);
    const updated = exists
      ? items.map((i) => (i.id === item.id ? item : i))
      : [...items, item];

    setItems(updated);
    setFilteredItems(updated);
    setModalOpen(false);
    setEditingItem(null);
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text("Inventario de Mobiliario", 10, 10);
    let y = 20;
    filteredItems.forEach((i, idx) => {
      doc.text(
        `${idx + 1}. ${i.nombre} | ${i.codigo} | ${i.ubicacion}`,
        10,
        y
      );
      y += 8;
    });
    doc.save("inventario.pdf");
  };

  const exportExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredItems);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Inventario");
    const buffer = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    saveAs(new Blob([buffer]), "inventario.xlsx");
  };

  if (!user) {
    return (
      <div className="login-container">
        <div className="login-box">
          <h2>Sistema de Inventario</h2>
          <p>Ingreso al sistema</p>
          <input placeholder="Usuario" onChange={(e) => setLogin({ ...login, username: e.target.value })} />
          <input type="password" placeholder="Contraseña" onChange={(e) => setLogin({ ...login, password: e.target.value })} />
          <button className="btn primary" onClick={handleLogin}>Ingresar</button>
        </div>
      </div>
    );
  }

  return (
    <div className="layout">
      <aside className="sidebar">
        <h3>Inventario</h3>
        <p className="role">{user.role.toUpperCase()}</p>
      </aside>

      <main className="content">
        <header className="topbar">
          <h2>Gestión de Mobiliario</h2>
          <button className="btn danger" onClick={() => setUser(null)}>Salir</button>
        </header>

        <div className="actions">
          {user.role !== "auxiliar" && (
            <button className="btn success" onClick={() => setModalOpen(true)}>+ Agregar</button>
          )}
          <button className="btn pdf" onClick={exportPDF}>PDF</button>
          <button className="btn excel" onClick={exportExcel}>Excel</button>
        </div>

        <table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Código</th>
              <th>Ubicación</th>
              <th>Estado</th>
               <th>Cantidad</th>
              {user.role !== "auxiliar" && <th>Acciones</th>}
            </tr>
          </thead>
          <tbody>
            {filteredItems.map((i) => (
              <tr key={i.id}>
                <td>{i.nombre}</td>
                <td>{i.codigo}</td>
                <td>{i.ubicacion}</td>
                <td>{i.cantidad}</td>
                <td className={`status ${i.estado?.toLowerCase()}`}>{i.estado}</td>
                {user.role !== "auxiliar" && (
                  <td>
                    <button className="btn small" onClick={() => { setEditingItem(i); setModalOpen(true); }}>Editar</button>
                    <button className="btn small danger" onClick={() => handleDelete(i.id)}>Eliminar</button>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </main>

      {modalOpen && <Modal onSave={saveItem} onClose={() => setModalOpen(false)} item={editingItem} />}
    </div>
  );
}

function Modal({ onSave, onClose, item }) {
  const [data, setData] = useState(
    item || { id: Date.now(), nombre: "", codigo: "", ubicacion: "", estado: "" }
  );

  return (
    <div className="modal">
      <div className="modal-box">
        <h3>{item ? "Editar" : "Agregar"} Mobiliario</h3>
        {Object.keys(data).map(
          (k) =>
            k !== "id" && (
              <input key={k} placeholder={k} value={data[k]} onChange={(e) => setData({ ...data, [k]: e.target.value })} />
            )
        )}
        <div className="modal-actions">
          <button className="btn success" onClick={() => onSave(data)}>Guardar</button>
          <button className="btn" onClick={onClose}>Cancelar</button>
        </div>
      </div>
    </div>
  );
}

export default App;
