import jsPDF from "jspdf";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import { useState } from "react";
import "./App.css";

function App() {
  const users = [
    { username: "admin", password: "admin123", role: "Administrador" },
    { username: "jefe", password: "jefe123", role: "Jefe" },
    { username: "auxiliar", password: "aux123", role: "Auxiliar" },
  ];

  const permissions = {
    Administrador: { add: true, edit: true, delete: true },
    Jefe: { add: true, edit: true, delete: true },
    Auxiliar: { add: false, edit: false, delete: false },
  };

  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // INVENTARIO
  const [items, setItems] = useState([
    {
      id: 1,
      nombre: "Computadora Dell",
      codigo: "PC-001",
      tipo: "Equipo",
      ubicacion: "Sala de cómputo",
      estado: "Bueno",
    },
  ]);
  <div className="actions">
  <button className="pdf" onClick={exportPDF}>
    Exportar PDF
  </button>
  <button className="excel" onClick={exportExcel}>
    Exportar Excel
  </button>
</div>


  // MODAL
  const [showModal, setShowModal] = useState(false);
  const [mode, setMode] = useState("add"); // add | edit | view
  const [currentItem, setCurrentItem] = useState(null);

  // FORM
  const emptyForm = {
    nombre: "",
    codigo: "",
    tipo: "",
    ubicacion: "",
    estado: "",
  };
  const [form, setForm] = useState(emptyForm);

  // FILTROS
  const [filters, setFilters] = useState(emptyForm);
  const [filteredItems, setFilteredItems] = useState(items);

  const handleLogin = () => {
    const user = users.find(
      (u) => u.username === username && u.password === password
    );
    if (!user) {
      setError("Usuario o contraseña incorrectos");
      return;
    }
    setCurrentUser(user);
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setCurrentUser(null);
  };

  const openModal = (type, item = null) => {
    setMode(type);
    setCurrentItem(item);
    setForm(item ? item : emptyForm);
    setShowModal(true);
  };

  const handleSave = () => {
    if (mode === "add") {
      setItems([...items, { ...form, id: Date.now() }]);
    } else if (mode === "edit") {
      setItems(items.map((i) => (i.id === currentItem.id ? form : i)));
    }
    setShowModal(false);
    setFilteredItems(items);
  };

 const handleDelete = (id) => {
  if (window.confirm("¿Eliminar mobiliario?")) {
    const updated = items.filter((i) => i.id !== id);
    setItems(updated);
    setFilteredItems(updated);
  }
};

  const applyFilters = () => {
    const result = items.filter((i) =>
      Object.keys(filters).every((key) =>
        i[key].toLowerCase().includes(filters[key].toLowerCase())
      )
    );
    setFilteredItems(result);
  };

  const clearFilters = () => {
    setFilters(emptyForm);
    setFilteredItems(items);
  };

  if (!loggedIn) {
    return (
      <div className="login">
        <h2>Sistema de Inventario</h2>
        <input placeholder="Usuario" onChange={(e) => setUsername(e.target.value)} />
        <input type="password" placeholder="Contraseña" onChange={(e) => setPassword(e.target.value)} />
        {error && <p className="error">{error}</p>}
        <button className="primary" onClick={handleLogin}>Ingresar</button>
      </div>
    );
  }

  const can = permissions[currentUser.role];

  return (
    <div className="app">
      <header className="header">
        <h2>Inventario de Biblioteca</h2>
        <div>
          {currentUser.role}
          <button className="logout" onClick={handleLogout}>Salir</button>
        </div>
      </header>

      {/* FILTROS */}
      <div className="filters">
        <input placeholder="Nombre / Código" onChange={(e) => setFilters({ ...filters, nombre: e.target.value })} />
        <input placeholder="Tipo" onChange={(e) => setFilters({ ...filters, tipo: e.target.value })} />
        <input placeholder="Ubicación" onChange={(e) => setFilters({ ...filters, ubicacion: e.target.value })} />
        <input placeholder="Estado" onChange={(e) => setFilters({ ...filters, estado: e.target.value })} />

        <button className="primary" onClick={applyFilters}>Aplicar filtro</button>
        <button onClick={clearFilters}>Limpiar filtro</button>
      </div>

      {can.add && (
        <button className="add" onClick={() => openModal("add")}>
          Agregar mobiliario
        </button>
      )}

      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Código</th>
            <th>Tipo</th>
            <th>Ubicación</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((item) => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.codigo}</td>
              <td>{item.tipo}</td>
              <td>{item.ubicacion}</td>
              <td>{item.estado}</td>
              <td>
                <button onClick={() => openModal("view", item)}>Ver</button>
                {can.edit && <button onClick={() => openModal("edit", item)}>Editar</button>}
                {can.delete && <button className="danger" onClick={() => handleDelete(item.id)}>Eliminar</button>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* MODAL */}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>
              {mode === "add" && "Agregar mobiliario"}
              {mode === "edit" && "Editar mobiliario"}
              {mode === "view" && "Detalle del mobiliario"}
            </h3>

            {Object.keys(form).map((key) => (
              <input
                key={key}
                placeholder={key.toUpperCase()}
                value={form[key]}
                disabled={mode === "view"}
                onChange={(e) => setForm({ ...form, [key]: e.target.value })}
              />
            ))}

            <div className="modal-actions">
              {mode !== "view" && <button className="primary" onClick={handleSave}>Guardar</button>}
              <button onClick={() => setShowModal(false)}>Cerrar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
const exportPDF = () => {
  const doc = new jsPDF();
  doc.text("Inventario de Biblioteca", 10, 10);

  let y = 20;
  filteredItems.forEach((item, index) => {
    doc.text(
      `${index + 1}. ${item.nombre} | ${item.codigo} | ${item.tipo} | ${item.ubicacion} | ${item.estado}`,
      10,
      y
    );
    y += 8;
  });

  doc.save("inventario.pdf");
};
const exportExcel = () => {
  const worksheet = XLSX.utils.json_to_sheet(filteredItems);
  const workbook = XLSX.utils.book_new();

  XLSX.utils.book_append_sheet(workbook, worksheet, "Inventario");

  const excelBuffer = XLSX.write(workbook, {
    bookType: "xlsx",
    type: "array",
  });

  const file = new Blob([excelBuffer], {
    type: "application/octet-stream",
  });

  saveAs(file, "inventario.xlsx");
};


export default App;
