import { useState } from "react";
import "./App.css";

function App() {
  const users = [
    { username: "admin", password: "admin123", role: "Administrador" },
    { username: "jefe", password: "jefe123", role: "Jefe" },
    { username: "auxiliar", password: "aux123", role: "Auxiliar" },
  ];

  const permissions = {
    Administrador: { add: true, edit: true, delete: true },
    Jefe: { add: true, edit: true, delete: true },
    Auxiliar: { add: false, edit: false, delete: false },
  };

  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  // INVENTARIO
  const [items, setItems] = useState([
    { id: 1, nombre: "Computadora Dell", area: "Sala de cómputo", estado: "Bueno" },
  ]);

  const [form, setForm] = useState({ nombre: "", area: "", estado: "" });
  const [editingId, setEditingId] = useState(null);

  const handleLogin = () => {
    const user = users.find(
      (u) => u.username === username && u.password === password
    );
    if (!user) {
      setError("Usuario o contraseña incorrectos");
      return;
    }
    setCurrentUser(user);
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setCurrentUser(null);
    setUsername("");
    setPassword("");
  };

  const handleSubmit = () => {
    if (!form.nombre || !form.area || !form.estado) return;

    if (editingId) {
      setItems(
        items.map((i) =>
          i.id === editingId ? { ...i, ...form } : i
        )
      );
      setEditingId(null);
    } else {
      setItems([...items, { ...form, id: Date.now() }]);
    }

    setForm({ nombre: "", area: "", estado: "" });
  };

  const handleEdit = (item) => {
    setForm(item);
    setEditingId(item.id);
  };

  const handleDelete = (id) => {
    if (window.confirm("¿Eliminar mobiliario?")) {
      setItems(items.filter((i) => i.id !== id));
    }
  };

  if (!loggedIn) {
    return (
      <div className="login">
        <h2>Sistema de Inventario</h2>
        <input placeholder="Usuario" onChange={(e) => setUsername(e.target.value)} />
        <input
          type="password"
          placeholder="Contraseña"
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <p className="error">{error}</p>}
        <button className="primary" onClick={handleLogin}>
          Ingresar
        </button>
      </div>
    );
  }

  const can = permissions[currentUser.role];

  return (
    <div className="app">
      <header className="header">
        <h2>Inventario de Biblioteca</h2>
        <div>
          <strong>{currentUser.role}</strong>
          <button className="logout" onClick={handleLogout}>
            Cerrar sesión
          </button>
        </div>
      </header>

      {can.add && (
        <div className="form">
          <input
            placeholder="Nombre del mobiliario"
            value={form.nombre}
            onChange={(e) => setForm({ ...form, nombre: e.target.value })}
          />
          <input
            placeholder="Área"
            value={form.area}
            onChange={(e) => setForm({ ...form, area: e.target.value })}
          />
          <select
            value={form.estado}
            onChange={(e) => setForm({ ...form, estado: e.target.value })}
          >
            <option value="">Estado</option>
            <option>Bueno</option>
            <option>Regular</option>
            <option>Malo</option>
          </select>

          <button className="add" onClick={handleSubmit}>
            {editingId ? "Actualizar" : "Agregar"}
          </button>
        </div>
      )}

      <table>
        <thead>
          <tr>
            <th>Mobiliario</th>
            <th>Área</th>
            <th>Estado</th>
            {(can.edit || can.delete) && <th>Acciones</th>}
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.nombre}</td>
              <td>{item.area}</td>
              <td>{item.estado}</td>
              {(can.edit || can.delete) && (
                <td>
                  {can.edit && (
                    <button className="primary" onClick={() => handleEdit(item)}>
                      Editar
                    </button>
                  )}
                  {can.delete && (
                    <button className="danger" onClick={() => handleDelete(item.id)}>
                      Eliminar
                    </button>
                  )}
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
