import { useState } from "react";
import "./App.css";

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [role, setRole] = useState("");

  // Permisos por rol
  const permissions = {
    Administrador: {
      add: true,
      edit: true,
      delete: true,
      export: true,
    },
    Jefe: {
      add: true,
      edit: true,
      delete: true,
      export: true,
    },
    Auxiliar: {
      add: false,
      edit: false,
      delete: false,
      export: false,
    },
  };

  // LOGIN
  if (!loggedIn) {
    return (
      <div className="login">
        <h2>Sistema de Inventario</h2>

        <select onChange={(e) => setRole(e.target.value)}>
          <option value="">Selecciona un rol</option>
          <option value="Administrador">Administrador</option>
          <option value="Jefe">Jefe</option>
          <option value="Auxiliar">Auxiliar</option>
        </select>

        <button
          className="primary"
          onClick={() => {
            if (role) setLoggedIn(true);
            else alert("Selecciona un rol");
          }}
        >
          Ingresar
        </button>
      </div>
    );
  }

  const can = permissions[role];

  // DASHBOARD
  return (
    <div className="app">
      <header className="header">
        <h2>Inventario de Biblioteca</h2>
        <div className="user">
          <span>{role}</span>
          <button className="logout" onClick={() => setLoggedIn(false)}>
            Cerrar sesión
          </button>
        </div>
      </header>

      <section className="actions">
        {can.add && (
          <button
            className="add"
            onClick={() => alert("Agregar mobiliario")}
          >
            Agregar mobiliario
          </button>
        )}

        {can.export && (
          <>
            <button
              className="pdf"
              onClick={() => alert("Exportar PDF")}
            >
              Exportar PDF
            </button>

            <button
              className="excel"
              onClick={() => alert("Exportar Excel")}
            >
              Exportar Excel
            </button>
          </>
        )}
      </section>

      <table>
        <thead>
          <tr>
            <th>Mobiliario</th>
            <th>Área</th>
            <th>Estado</th>
            {(can.edit || can.delete) && <th>Acciones</th>}
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Computadora Dell</td>
            <td>Sala de cómputo</td>
            <td><span className="status good">Bueno</span></td>

            {(can.edit || can.delete) && (
              <td>
                {can.edit && (
                  <button
                    className="primary"
                    onClick={() => alert("Editar")}
                  >
                    Editar
                  </button>
                )}
                {can.delete && (
                  <button
                    className="danger"
                    onClick={() => alert("Eliminar")}
                  >
                    Eliminar
                  </button>
                )}
              </td>
            )}
          </tr>
        </tbody>
      </table>

      {role === "Jefe" && (
        <p className="note">
          * El acceso al código fuente está restringido por políticas del sistema.
        </p>
      )}
    </div>
  );
}

export default App;
