import { useState } from "react";
import "./App.css";

function App() {
  // Usuarios simulados
  const users = [
    { username: "admin", password: "admin123", role: "Administrador" },
    { username: "jefe", password: "jefe123", role: "Jefe" },
    { username: "auxiliar", password: "aux123", role: "Auxiliar" },
  ];

  const permissions = {
    Administrador: { add: true, edit: true, delete: true, export: true },
    Jefe: { add: true, edit: true, delete: true, export: true },
    Auxiliar: { add: false, edit: false, delete: false, export: false },
  };

  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  // Login inputs
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = () => {
    const user = users.find(
      (u) => u.username === username && u.password === password
    );

    if (!user) {
      setError("Usuario o contraseña incorrectos");
      return;
    }

    setCurrentUser(user);
    setLoggedIn(true);
    setError("");
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setCurrentUser(null);
    setUsername("");
    setPassword("");
  };

  // LOGIN VIEW
  if (!loggedIn) {
    return (
      <div className="login">
        <h2>Sistema de Inventario</h2>

        <input
          type="text"
          placeholder="Usuario"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {error && <p className="error">{error}</p>}

        <button className="primary" onClick={handleLogin}>
          Ingresar
        </button>

        <div className="hint">
          <strong>Usuarios de prueba:</strong>
          <br />
          admin / admin123
          <br />
          jefe / jefe123
          <br />
          aux123 / aux123
        </div>
      </div>
    );
  }

  const can = permissions[currentUser.role];

  // DASHBOARD
  return (
    <div className="app">
      <header className="header">
        <h2>Inventario de Biblioteca</h2>
        <div className="user">
          <span>{currentUser.role}</span>
          <button className="logout" onClick={handleLogout}>
            Cerrar sesión
          </button>
        </div>
      </header>

      <section className="actions">
        {can.add && (
          <button className="add" onClick={() => alert("Agregar mobiliario")}>
            Agregar mobiliario
          </button>
        )}

        {can.export && (
          <>
            <button className="pdf" onClick={() => alert("Exportar PDF")}>
              Exportar PDF
            </button>
            <button className="excel" onClick={() => alert("Exportar Excel")}>
              Exportar Excel
            </button>
          </>
        )}
      </section>

      <table>
        <thead>
          <tr>
            <th>Mobiliario</th>
            <th>Área</th>
            <th>Estado</th>
            {(can.edit || can.delete) && <th>Acciones</th>}
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Computadora Dell</td>
            <td>Sala de cómputo</td>
            <td>
              <span className="status good">Bueno</span>
            </td>

            {(can.edit || can.delete) && (
              <td>
                {can.edit && (
                  <button
                    className="primary"
                    onClick={() => alert("Editar")}
                  >
                    Editar
                  </button>
                )}
                {can.delete && (
                  <button
                    className="danger"
                    onClick={() => alert("Eliminar")}
                  >
                    Eliminar
                  </button>
                )}
              </td>
            )}
          </tr>
        </tbody>
      </table>

      {currentUser.role === "Jefe" && (
        <p className="note">
          * El acceso al código fuente está restringido por políticas del sistema.
        </p>
      )}
    </div>
  );
}

export default App;
